import React, { useState, useEffect } from 'react';
import { Link } from "react-router-dom";

function ShoppingCartTable() {
  const [cartItems, setCartItems] = useState([]);
  const userId = localStorage.getItem("id");
  const token = localStorage.getItem("token");

  useEffect(() => {
    const fetchCartItems = async () => {
      if (!userId || !token) {
        console.error("User ID or Token is missing");
        return;
      }
      try {
        const response = await fetch(`http://127.0.0.1:5000/shoppingcart/${userId}`, {
          headers: {
            Authorization: `Bearer ${token}`,
            "Content-Type": "application/json",
          },
        });
        console.log("User ID:", localStorage.getItem("id"));
        console.log("Token:", localStorage.getItem("token"));
        if (!response.ok) {
          const errorText = await response.text();
          throw new Error(`Failed to fetch cart: ${errorText}`);
        }

        const data = await response.json();
        console.log("Cart Data:", data);
        setCartItems(data.cart || []); // Ensure it's an array
      } catch (error) {
        console.error("Error fetching shopping cart items:", error);
      }
    };

    if (userId && token) {
      fetchCartItems();
    }
  }, [userId, token]); // Dependency array to refetch when userId or token changes

  const handleDeleteItem = async (jobId) => {
    try {
      const response = await fetch(`http://127.0.0.1:5000/shoppingcart/${userId}/${jobId}`, {
        method: "DELETE",
        headers: {
          Authorization: `Bearer ${token}`,
          "Content-Type": "application/json",
        },
      });

      if (!response.ok) {
        const errorText = await response.text();
        throw new Error(`Failed to delete item: ${errorText}`);
      }

      setCartItems(prevItems => prevItems.filter(item => item.id !== jobId));
      alert("Deleted item from shopping cart");
    } catch (error) {
      console.error("Error deleting item from shopping cart:", error);
    }
  };

  return (
    <div className="shopping-cart-page">
      <h2>Shopping Cart</h2>
      {cartItems.length === 0 ? (
        <p>Your cart is empty.</p>
      ) : (
        <ul>
          {cartItems.map(item => (
            <li key={item.id}>
              <p>Title: {item.title}</p>
              <p>Description: {item.description}</p>
              <p>Cost: ${item.cost}</p>
              <button onClick={() => handleDeleteItem(item.id)}>Delete</button>
            </li>
          ))}
        </ul>
      )}
      <button className="entry-login-btn">
        <Link to="/checkout" className="link">Proceed to Checkout</Link>
      </button>
    </div>
  );
}

export default ShoppingCartTable;
