import React, { useState, useEffect, useCallback, useRef } from "react";

function CheckoutData() {
  const [cartItems, setCartItems] = useState([]);
  const userId = localStorage.getItem("id");
  const tokenRef = useRef(localStorage.getItem("access_token"));
  const refreshTokenValue = localStorage.getItem("refresh_token");

  // ðŸ”¹ Function to refresh token
  const refreshToken = useCallback(async () => {
    try {
      const response = await fetch("http://127.0.0.1:5000/refresh", {
        method: "POST",
        headers: {
          Authorization: `Bearer ${refreshTokenValue}`,
          "Content-Type": "application/json",
        },
      });

      if (!response.ok) {
        console.error("Failed to refresh token, logging out...");
        localStorage.removeItem("access_token");
        localStorage.removeItem("refresh_token");
        return false;
      }

      const data = await response.json();
      localStorage.setItem("access_token", data.access_token);
      tokenRef.current = data.access_token;
      console.log("Token refreshed!");
      return true;
    } catch (error) {
      console.error("Error refreshing token:", error);
      return false;
    }
  }, [refreshTokenValue]);

  // ðŸ”¹ Function to fetch cart items
  const fetchCartItems = useCallback(async () => {
    if (!userId || !tokenRef.current) {
      console.error("User ID or Token is missing");
      return;
    }
    try {
      let response = await fetch(`http://127.0.0.1:5000/shoppingcart/${userId}`, {
        headers: {
          Authorization: `Bearer ${tokenRef.current}`,
          "Content-Type": "application/json",
        },
      });

      if (response.status === 401) {
        console.warn("Token expired, attempting to refresh...");
        const refreshed = await refreshToken();
        if (refreshed) {
          response = await fetch(`http://127.0.0.1:5000/shoppingcart/${userId}`, {
            headers: {
              Authorization: `Bearer ${tokenRef.current}`,
              "Content-Type": "application/json",
            },
          });
        }
      }

      if (!response.ok) {
        throw new Error("Failed to fetch cart");
      }

      const data = await response.json();
      setCartItems(data.cart || []);
    } catch (error) {
      console.error("Error fetching shopping cart items:", error);
    }
  }, [userId, refreshToken]);

  useEffect(() => {
    fetchCartItems();
  }, [fetchCartItems]);

  // ðŸ”¹ Function to submit all items
  const handleCheckout = async () => {
    if (cartItems.length === 0) {
      alert("No items to checkout!");
      return;
    }

    try {
      const formData = cartItems.map(item => ({
        title: item.title,
        description: item.description,
        cost: item.cost,
      }));

      const token = tokenRef.current;
      const responses = await Promise.all([
        fetch("http://127.0.0.1:5000/userpurchasedtask", {
          method: "POST",
          body: JSON.stringify({ items: formData }),
          headers: {
            "Content-Type": "application/json",
            Authorization: `Bearer ${token}`,
          },
        }),
        fetch("http://127.0.0.1:5000/receipt", {
          method: "POST",
          body: JSON.stringify({ items: formData }),
          headers: {
            "Content-Type": "application/json",
            Authorization: `Bearer ${token}`,
          },
        })
      ]);

      if (responses.every(response => response.ok)) {
        alert("Checkout successful!");
        setCartItems([]);
      } else {
        throw new Error("Failed to process checkout");
      }
    } catch (error) {
      console.error("Error during checkout:", error);
    }
  };

  return (
    <div className="shopping-cart-page">
      <h2>Checkout Items</h2>
      {cartItems.length === 0 ? (
        <p>There are no items to checkout.</p>
      ) : (
        <>
          <ul>
            {cartItems.map((item, index) => (
              <li key={index}>
                <p>Title: {item.title}</p>
                <p>Description: {item.description}</p>
                <p>Cost: ${item.cost}</p>
              </li>
            ))}
          </ul>
          <h3>Total Cost: ${cartItems.reduce((total, item) => total + (item.cost || 0), 0).toFixed(2)}</h3>
          <button className="btn btn-primary" onClick={handleCheckout}>Checkout</button>
        </>
      )}
    </div>
  );
}

export default CheckoutData;
