import React, { useState, useEffect, useCallback } from "react";

function CheckoutData() {
  const [cartItems, setCartItems] = useState([]);
  const [isSubmitting, setIsSubmitting] = useState(false);
  const userId = localStorage.getItem("id");
  let token = localStorage.getItem("access_token");
  const refreshTokenValue = localStorage.getItem("refresh_token");

  console.log("User ID:", userId);
  console.log("Token:", token);

  // ðŸ”¹ Function to refresh token
  const refreshToken = async () => {
    try {
      const response = await fetch("http://127.0.0.1:5000/refresh", {
        method: "POST",
        headers: {
          Authorization: `Bearer ${refreshTokenValue}`,
          "Content-Type": "application/json",
        },
      });

      if (!response.ok) {
        console.error("Failed to refresh token, logging out...");
        localStorage.removeItem("access_token");
        localStorage.removeItem("refresh_token");
        return false;
      }

      const data = await response.json();
      localStorage.setItem("access_token", data.access_token);
      token = data.access_token;
      console.log("Token refreshed!");
      return true;
    } catch (error) {
      console.error("Error refreshing token:", error);
      return false;
    }
  };

  // ðŸ”¹ Function to fetch cart items
  const fetchCartItems = useCallback(async () => {
    if (!userId || !token) {
      console.error("User ID or Token is missing");
      return;
    }
    try {
      let response = await fetch(`http://127.0.0.1:5000/shoppingcart/${userId}`, {
        headers: {
          Authorization: `Bearer ${token}`,
          "Content-Type": "application/json",
        },
      });

      if (response.status === 401) { // If token expired, refresh it
        console.warn("Token expired, attempting to refresh...");
        const refreshed = await refreshToken();
        if (refreshed) {
          response = await fetch(`http://127.0.0.1:5000/shoppingcart/${userId}`, {
            headers: {
              Authorization: `Bearer ${token}`,
              "Content-Type": "application/json",
            },
          });
        }
      }

      if (!response.ok) {
        const errorText = await response.text();
        throw new Error(`Failed to fetch cart: ${errorText}`);
      }

      const data = await response.json();
      if (!Array.isArray(data.cart)) {
        console.error("Unexpected data format:", data);
        return;
      }
      setCartItems([...data.cart]);
    } catch (error) {
      console.error("Error fetching shopping cart items:", error);
    }
  }, [userId, token]);

  useEffect(() => {
    if (userId && token) {
      fetchCartItems();
    }
  }, [userId, token, fetchCartItems]);

  // ðŸ”¹ Function to calculate total cost
  const calculateTotalCost = () => {
    return cartItems.reduce((total, item) => total + (item.cost || 0), 0);
  };

  // ðŸ”¹ Submit all items to both receipts and userpurchased tasks
  const handleSubmitAll = async () => {
    if (cartItems.length === 0) {
      alert("Your cart is empty!");
      return;
    }

    setIsSubmitting(true);
    const token = localStorage.getItem("access_token");
    if (!userId || !token) {
      alert("User not authenticated.");
      setIsSubmitting(false);
      return;
    }

    const payload = cartItems.map(item => ({
      title: item.title,
      description: item.description,
      cost: item.cost,
    }));

    try {
      // ðŸ”¹ Send all items to `userpurchasedtask`
      const response1 = await fetch("http://127.0.0.1:5000/userpurchasedtask", {
        method: "POST",
        body: JSON.stringify(payload),
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`,
        },
      });

      if (!response1.ok) throw new Error("Failed to add items to user purchased tasks.");

      // ðŸ”¹ Send all items to `receipt`
      const response2 = await fetch("http://127.0.0.1:5000/receipt", {
        method: "POST",
        body: JSON.stringify(payload),
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`,
        },
      });

      if (!response2.ok) throw new Error("Failed to add items to receipt.");

      alert("All items successfully added to receipt and user purchased tasks!");
      setCartItems([]); // Clear cart after successful submission
    } catch (error) {
      console.error("Error submitting items:", error);
      alert("An error occurred while submitting. Please try again.");
    } finally {
      setIsSubmitting(false);
    }
  };

  return (
    <div className="shopping-cart-page">
      <h2>Checkout Items</h2>
      {cartItems.length === 0 ? (
        <p>No items to checkout.</p>
      ) : (
        <>
          <ul>
            {cartItems.map((item, index) => (
              <li key={item.id || index}>
                <p>Title: {item.title}</p>
                <p>Description: {item.description}</p>
                <p>Cost: ${item.cost}</p>
              </li>
            ))}
          </ul>
          <h3>Total Cost: ${calculateTotalCost().toFixed(2)}</h3>
          <button className="btn btn-primary" onClick={handleSubmitAll} disabled={isSubmitting}>
            {isSubmitting ? "Submitting..." : "Submit All to Receipt & Purchased Tasks"}
          </button>
        </>
      )}
    </div>
  );
}

export default CheckoutData;
